mapper.update:
UPDATE market_comment 
SET market_commnet_tb_step = market_commnet_tb_step + 1
WHERE market_commnet_tb_ref = ${marketCommentTbRef} AND market_commnet_tb_step > ${marketCommentTbStep}

mapper.select:
SELECT COUNT(*)
FROM market_comment
WHERE market_tb_no = ${marketTbNo}

mapper.update:
UPDATE market_comment
SET
market_comment_tb_content = #{marketCommentTbContent}
WHERE
market_comment_tb_no = #{marketCommentTbNo}
