mapper.update:
UPDATE market
SET market_tb_read_count = market_tb_read_count + 1
WHERE market_tb_no = #{marketTbNo}

mapper.select:
SELECT * FROM (SELECT ROWNUM rnum, market_tb_no, customer_tb_no, market_tb_title, market_tb_content, market_tb_reg_date, market_tb_read_count, market_tb_sell_or_buy, market_tb_img_path FROM (SELECT * FROM market
WHERE market_tb_sell_or_buy = #{marketTbSellOrBuy}
ORDER BY market_tb_no DESC)) WHERE rnum >= #{startRow} AND rnum <= #{endRow}

mapper.update:
UPDATE market
SET
market_tb_title = #{marketTbTitle},
market_tb_content = #{marketTbContent},
market_tb_sell_or_buy = #{marketTbSellOrBuy}

, market_tb_img_path = #{marketTbImgPath}

WHERE
market_tb_no = #{marketTbNo}
