mapper.update:
UPDATE pd_sale
SET
    PD_SALE_TB_STATE=#{pdSaleTbState}
WHERE PD_SALE_TB_NO=#{pdSaleTbNo}

UPDATE PD_SALE
SET
    PD_SALE_TB_REMAINING_AMOUNT = PD_SALE_TB_REMAINING_AMOUNT - #{ordersDetailTbAmount}
WHERE PD_SALE_TB_NO = #{pdSaleTbNo}

UPDATE PD_SALE
SET
    PD_SALE_TB_REMAINING_AMOUNT = PD_SALE_TB_REMAINING_AMOUNT + #{ordersDetailTbAmount}
WHERE PD_SALE_TB_NO = #{pdSaleTbNo}
